mapper.insert:
INSERT INTO application_status(
status_uuid,
course_uuid,
status
)
VALUES(
#{statusUuid},
#{courseUuid},
#{status}
)

mapper.select:
SELECT DISTINCT s.* FROM students s
LEFT JOIN student_courses sc ON s.student_uuid = sc.student_uuid
LEFT JOIN application_status aps ON sc.course_uuid = aps.course_uuid



      AND s.name LIKE CONCAT('%', #{student.name}, '%')


      AND s.is_deleted = #{student.isDeleted}



    AND sc.course_name = #{course.courseName}


    AND aps.status = #{status.status}

mapper.update:
UPDATE student_courses
SET course_name = #{courseName},
is_deleted = #{isDeleted}
WHERE course_uuid = #{courseUuid}

UPDATE application_status
SET status = #{status}
WHERE course_uuid = #{courseUuid}
